import React from "react";
import * as Tone from "tone";

export const Synth = () => {
  const webMIDI = navigator.requestMIDIAccess.then(
    onMIDISuccess,
    onMIDIFailure
  );

  webMIDI
    ? console.log("WebMIDI is supported")
    : console.log("WebMIDI is not supported.");

  console.log(webMIDI);

  //   const onMIDISuccess = (midiAccess) => {
  //     console.log(midiAccess);
  //     let inputs = midiAccess.inputs;
  //     let outputs = midiAccess.outputs;
  //   };

  //   const onMIDIFailure = () => {
  //     console.log("Could not access a MIDI device.");
  //   };

  const synth = new Tone.Synth().toDestination();

  const playC = () => {
    synth.triggerAttackRelease("C4", "8n");
  };

  return (
    <div>
      <button name="play-tone" id="play-tone" onClick={playC}>
        Play C4
      </button>
    </div>
  );
};
