import React from "react";
import * as Tone from "tone";

export const Synth = () => {
  // const webMIDI = navigator.requestMIDIAccess();

  // webMIDI
  //   ? console.log("WebMIDI is supported")
  //   : console.log("WebMIDI is not supported.");

  // console.log(navigator);

  const MIDIinit = async () => {
    try {
      const midiAccess = await navigator.requestMIDIAccess();
      onMIDISuccess(midiAccess);
    } catch (err) {
      onMIDIFailure(err);
    }
  };

  const onMIDISuccess = (obj) => {
    console.log(obj);
    let inputs = obj.inputs;
    let outputs = obj.outputs;
  };

  const onMIDIFailure = () => {
    console.log("Could not access your MIDI devices.");
  };

  MIDIinit();

  const synth = new Tone.Synth().toDestination();

  const playC = () => {
    synth.triggerAttackRelease("C4", "8n");
  };

  return (
    <div>
      <button name="play-tone" id="play-tone" onClick={playC}>
        Play C4
      </button>
    </div>
  );
};
